$(document).ready(function() {
	$(window).scroll(function() {
		if ($(this).scrollTop() >= 100) {
			$('.header-body').addClass('fix-head');
		} else if($(this).scrollTop() < 100) {
			$('.header-body').removeClass('fix-head');
		}
	});

	$(".navBtn").click(function() {
		$('.navBtn, .navHead-content').toggleClass('open');
		// $('.header-nav').slideUp('slow');
		// if ( $('.header-nav').first().is( ":hidden" ) ) {
		// 	$('.header-nav').show( "slow" );
		// } else {
		// 	$('.header-nav').slideUp();
		// }
	});

	$( ".faq-title" ).click(function() {
        $(this).next().slideUp();
        $(this).toggleClass('open');
        if ( $(this).next().is( ":hidden" ) ) {
            $(this).next().slideDown( "slow" );
        } else {
            $(this).next().slideUp();
        }
    });

	$( ".openDetails" ).click(function() {
		// $(this).next().slideUp();
        $(this).toggleClass('open');
        $(this).parent().parent().toggleClass('open');
        if ( $(this).parent().parent().next().is( ":hidden" ) ) {
            $(this).parent().parent().next().slideDown( "slow" );
        } else {
            $(this).parent().parent().next().slideUp();
        }
    });

	$(".tab_content").hide();
	$("ul.tabs li:first").addClass("current").show();
	$(".tab_content:first").show();
	$("ul.tabs li").click(function() {
		$("ul.tabs li").removeClass("current");
		$(this).addClass("current");
		$(".tab_content").hide();
		var activeTab = $(this).find("a").attr("href");
		$(activeTab).fadeIn();
		return false;
	});

	$(".scroll-top").click(function(){
		$("html, body").animate({ scrollTop: 0 }, "slow");
  		return false;
	});
});
function copyToClipboard(el) {

	// resolve the element
	el = (typeof el === 'string') ? document.querySelector(el) : el;

	// handle iOS as a special case
	if (navigator.userAgent.match(/ipad|ipod|iphone/i)) {

		// save current contentEditable/readOnly status
		var editable = el.contentEditable;
		var readOnly = el.readOnly;

		// convert to editable with readonly to stop iOS keyboard opening
		el.contentEditable = true;
		el.readOnly = true;

		// create a selectable range
		var range = document.createRange();
		range.selectNodeContents(el);

		// select the range
		var selection = window.getSelection();
		selection.removeAllRanges();
		selection.addRange(range);
		el.setSelectionRange(0, 999999);

		// restore contentEditable/readOnly to original state
		el.contentEditable = editable;
		el.readOnly = readOnly;
		document.getElementById('btnCopyAdress').innerHTML = "<span> √ </span>";
		
		setTimeout(function() { document.getElementById('btnCopyAdress').innerHTML = "<span> </span>"; }, 2000);
	}
	else {
		el.select();
		document.getElementById('btnCopyAdress').innerHTML = "<span> √ </span>";

		setTimeout(function() { document.getElementById('btnCopyAdress').innerHTML = "<span> </span>"; }, 2000);
	}

	// execute copy command
	document.execCommand('copy');
}	